{ inAppWallet }
from
"thirdweb/wallets"
;
import
{ sepolia }
from
"thirdweb/chains"
;
function
App
() {
// 1. set the `accountAbstraction` configuration
const
{
connect
}
=
useConnect
({
client,
accountAbstraction: {
chain: sepolia,
// the chain where your smart accounts will be or is deployed
sponsorGas:
true
,
// enable or disable sponsored transactions
},
});
const
connectToSmartAccount
=
async
()
=>
{
// 2. connect with the admin wallet of the smart account
connect
(
async
()
=>
{
const
wallet
=
inAppWallet
();
// or any other wallet
await
wallet. connect
({
client,
chain: sepolia,
strategy:
"google"
,
});
return
wallet;
});
};
return
(
<
button
onClick
=
{
()
=>
connectToSmartAccount
()
}
>Connect</
button
>
);
}
